import React from 'react';

class Car extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            brand: "Ford",
        }
    }

    static getDerivedStateFromProps(props, state) {
        return {brand: props.newbrand};
    } 

    componentDidMount() {
        setTimeout(() => {
          this.setState({brand: "yellow"})
        }, 1000)
    }

    render() {
        return (   
        <div>
             <h2>I'm a { this.state.brand } </h2>
        </div>     
        );   
    }
}

export default Car;
