import React, {Component} from 'react';
import Person from './components/Person';


class App extends Component {
    state = {
        persons: [
            {name: 'y', age: 28},
            {name: 'John', age: 35}
        ]
    }

    switchNameHandler = (newName) => {
        this.setState({
            persons: [
                {name: newName, age: 28},
                {name: 'John', age: 35}
            ]
        });
    }

    changeNameHandler = (event) => {
        this.setState({
            persons: [
                {name: event.target.value, age: 28},
                {name: 'John', age: 35}
            ]
        });
    }

    render() {
        return(
            <div>
                <h1>React</h1>
                <button onClick = { () => this.switchNameHandler('Maxiiii') }>Switch Name</button>
                <Person changed = { this.changeNameHandler } click = {this.switchNameHandler.bind(this, 'Hiiiii')} name = {this.state.persons[0].name} age = {this.state.persons[0].age}/>
                <Person name = {this.state.persons[1].name} age = {this.state.persons[1].age}/>
            </div>
        );
    }
}

export default App;
