import React from 'react';
import Person from '../person/Person';
import './persons.scss';



let isFirstPersonClicked = false;

const showChilds = (personId) => (event) => {
    if (personId === 1) {
         isFirstPersonClicked = true;
    }
}

const Persons = () => {
    return (
                <div className="persons">

                    <div onClick={showChilds(1)}>
                        <Person/>
                    </div>

                     { isFirstPersonClicked ? 
                       <div className="persons__second-childs">
                            <Person personId="2"/>
                            <Person personId="3"/>
                       </div> : 
                     null }


                    <div className="persons__third-child">
                        <Person personId="4"/>
                    </div>

                    <div className="persons__four-child">
                        <Person personId="5"/>
                    </div>

                </div>

    );
};

export default Persons;
