import React from 'react';
import './persontabs.scss';



const data = [
    {id : '1',
     tabTitle: "Şəxsi İnformasiya",
     tabContentTitle: "",
     tabContent: 'Tab Content 1'
    },
    {id : '2',
     tabTitle: "Struktur Əsasnaməsi",
     tabContentTitle: "",
     tabContent: 'Tab Content 2'
    },
    {id : '3',
     tabTitle: "Vəzifə Təlimatı",
     tabContentTitle: "",
     tabContent: 'Tab Content 3'
    }
  ]

  function Tab(props){

    const [visibleTab, setVisibleTab] = React.useState(props.data[0].id)

    const listTitles = props.data.map((item) => 
        <li onClick={() => setVisibleTab(item.id)} className={visibleTab === item.id ? "tab-title tab-title--active" : "tab-title"}>{item.tabTitle}</li>
    )       

    const listContent = props.data.map((item) => 
        <div style={visibleTab === item.id ? {} : {display: 'none'}}>
            <h2>{item.tabContentTitle}</h2>
            <p>{item.tabContent}</p>
        </div>
    )

    return(
        <div className="tabs">
          <ul className="tabs-titles">
            {listTitles}
          </ul>
          <div className="tab-content">
             {listContent}
          </div>
        </div>
      )
  }



const Persontabs = () => {
    return (
        <div className="persontabs">
            <Tab data={data}/>
        </div>
    );
}


export default Persontabs;
